const zoneId = $zoneId

// CAMBIO: Construimos la URL con un template literal y encodeURIComponent
// Esto evita el error "URL is not defined" y codifica correctamente el parámetro.
const baseUrl = 'https://api-office-manager-pre.azurewebsites.net/api/Desk/GetAvailability';
const url = `${baseUrl}?officeId=${encodeURIComponent(officeId)}&startDate=${encodeURIComponent(startDate)}&endDate=${encodeURIComponent(endDate)}&zoneId=${encodeURIComponent(zoneId)}`;

console.log(`Realizando llamada a: ${url}`); // Línea de depuración útil

const options = {
    method: 'GET',
    headers: {
        'Accept': 'application/json',
        'Authorization': accessToken,
    }
};

try {
    const response = await fetch(url, options);

    // Comprobamos si la respuesta fue exitosa
    if (!response.ok) {
        // Si hay un error, intentamos leer el texto para tener más detalles
        const errorText = await response.text();
        console.error(`Error de la API: ${response.status} ${response.statusText}`, errorText);
        return { error: `La API devolvió un error: ${response.status}`, details: errorText };
    }

    const data = await response.json();
    return data;

} catch (error) {
    console.error('Error en la función fetch:', error);
    return { error: 'Ha ocurrido un error en la llamada a la API.', details: error.message };
