02},"dragging":false},{"id":"customFunctionAgentflow_0","position":{"x":292.08521875,"y":90.253375},"data":{"loadMethods":{},"label":"Limpiar Response de Incidencias","name":"customFunctionAgentflow","version":1,"type":"CustomFunction","category":"Agent Flows","description":"Execute custom function","baseClasses":["CustomFunction"],"color":"#E4B7FF","inputs":{"customFunctionInputVariables":[{"variableName":"input","variableValue":"<p><span class="variable" data-type="mention" data-id="httpAgentflow_0" data-label="httpAgentflow_0">{{ httpAgentflow_0 }}</span> </p>"}],"customFunctionJavascriptFunction":"// Extrae incidencias (id, title y path) de distintos formatos de entrada
function extractIncidencesFromAny(input) {
  // 1) Parseo inicial (si viene como string)
  let parsed;
  try {
    parsed = (typeof input === "string") ? JSON.parse(input) : input;
  } catch (e) {
    return "Error al procesar la entrada: El texto recibido no es un JSON válido.";
  }

  // 2) Si viene envuelto como { variableName, variableValue: "..." }, parsear variableValue
  if (parsed && typeof parsed.variableValue === "string") {
    try {
      parsed = JSON.parse(parsed.variableValue);
    } catch (e) {
      return "Error: 'variableValue' no es un JSON válido.";
    }
  }

  // 3) Normalizar a array de incidencias
  let items = [];
  if (Array.isArray(parsed)) {
    items = parsed; // formato: [...]
  } else if (parsed && Array.isArray(parsed.data)) {
    items = parsed.data; // formato: { data: [...] }
  } else {
    return []; // nada reconocible
  }

  // Helper para priorizar ES > EN > canonical
  const pref = (obj) =>
    obj?.text?.["es-es"] ??
    obj?.text?.["en-en"] ??
    obj?.canonicalText ??
    "";

  // 4) Mapear al formato pedido
  return items.map(it => ({
    id: it?.id ?? "",
    title: it?.title ?? "",
    path: (it?.path ?? []).map(p => ({
      title: pref(p?.title),
      value: pref(p?.value),
    })),
  }));
}

// --- USO EN TU ENTORNO ---
const result = extractIncidencesFromAny($input);
return result;
